import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Title = (props) => {
  const text = {props}
  return(
    <div>
      <h2>
        {text}
      </h2>
    </div>
  )
}

const Button = (props) => {
  const {text, functionality} = props
  return(
    <div>
      <button value={text} onClick={() => functionality}></button>
    </div>
  )
}

const CommentsQuantity = (props) => {
  const {comment, quantity} = props
  return(
    <div>
      <p>{comment} {quantity}</p>
    </div>
  )
}

const Stadistics = (props) => {
  const quatities = {props}
  return(
    <div>
      <CommentsQuantity comment="good" quantity={0/*quantities[0]*/}/>
      <CommentsQuantity comment="neutral" quantity={1/*quantities[1]*/}/>
      <CommentsQuantity comment="bad" quantity={2/*quantities[2]*/}/>
    </div>
  )
}  

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
    <div>
      <Title text="give feedback"/>
      <Button text="good" functionality={() => setGood(good + 1)}/>
      <Button text="neutral" functionality={() => setNeutral(neutral +1)}/>
      <Button text="bad" functionality={() => setBad(bad + 1)}/>
      <Title text="stadistics"/>
      <Stadistics quantities={[good, neutral, bad]}/>
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
