import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Title = (props) => {
  const {text} = props
  return(
    <div>
      <h2>
        {text}
      </h2>
    </div>
  )
}

const Button = (props) => {
  const {text, functionality} = props
  return(
    <div>
      <button onClick={() => functionality()}>{text}</button>
    </div>
  )
}

const CommentsQuantity = (props) => {
  const {comment, quantity} = props
  return(
    <div>
      <p>{comment} {quantity}</p>
    </div>
  )
}

const Stadistics = (props) => {
  const {names, quantities} = props

  function sum(array){
    let acumulator = 0
    for(let i=0; i<array.length; i++)
      acumulator += array[i]
    return acumulator
  }
  const total = sum(quantities)

  function average(array){
    let total_per_value = sum([array[0]*1, array[1]*0, array[2]*-1])
    let total = sum(array)
    return total == 0 ? 0 : total_per_value/total
  }
  const a = average(quantities)

  function positive_percent(array){
    let total = sum(array)
    return total == 0 ? 0 : array[0] * 100 / total
  }
  const pp = positive_percent(quantities).toString().concat(" %")

  return(
    <div>
      <CommentsQuantity comment={names[0]} quantity={quantities[0]}/>
      <CommentsQuantity comment={names[1]} quantity={quantities[1]}/>
      <CommentsQuantity comment={names[2]} quantity={quantities[2]}/>
      <CommentsQuantity comment={names[3]} quantity={total}/>
      <CommentsQuantity comment={names[4]} quantity={a}/>
      <CommentsQuantity comment={names[5]} quantity={pp}/>
    </div>
  )
}  

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)  
  const comments = ["good", "neutral", "bad", "all", "average", "positive"]
  const quantities = [good, neutral, bad]

  const total = () => quantities.reduce((acumulador, suma) => acumulador + suma, 0);

  return (
    <div>
      <Title text="give feedback"/>
      <Button text={comments[0]} functionality={() => setGood(good + 1)}/>
      <Button text={comments[1]} functionality={() => setNeutral(neutral +1)}/>
      <Button text={comments[2]} functionality={() => setBad(bad + 1)}/>
      <Title text="stadistics"/>
      <Stadistics names={comments} quantities={quantities}/>
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
