import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Title = (props) => {
  const text = {props}
  return(
    <div>
      <h2>
        text
      </h2>
    </div>
  )
}

const Button = (props) => {
  const {name_button, incrementComment} = props
  return(
    <div>
      <button value={name_button} onClick={() => incrementComment}></button>
    </div>
  )
}

const CommentsQuantity = (props) => {
  const {comment, quantity} = props
  return(
    <div>
      <p>{comment} {quantity}</p>
    </div>
  )
}

const Stadistics = () => {
  return(
    <div>
      <CommentsQuantity comment="good"/>
      <CommentsQuantity comment="neutral"/>
      <CommentsQuantity comment="bad"/>
    </div>
  )
}  

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
    <div>
      code here
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
