import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Title = (props) => {
  const {text} = props
  return(
    <div>
      <h2>
        {text}
      </h2>
    </div>
  )
}

const Button = (props) => {
  const {text, functionality} = props
  return(
    <div>
      <button onClick={() => functionality}>{text}</button>
    </div>
  )
}

const CommentsQuantity = (props) => {
  const {comment, quantity} = props
  return(
    <div>
      <p>{comment} {quantity}</p>
    </div>
  )
}

const Stadistics = (props) => {
  console.log(props)
  const {names, quatities} = props
  return(
    <div>
      <CommentsQuantity comment={names[0]} quantity={0}/>
      <CommentsQuantity comment={names[1]} quantity={1/*quantities[1]*/}/>
      <CommentsQuantity comment={names[2]} quantity={2/*quantities[2]*/}/>
    </div>
  )
}  

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const comments_data = {
    names: ["good", "neutral", "bad"],
    quatities: [good, neutral, bad]
